import { Question, QuestionCategory } from '@/types/game';

// Firestore'da soru yapısı için tip
export interface FirestoreQuestion {
  category: string;
  question: string;
  options: string[];
  answer: string;
  difficulty: number;
  used: boolean;
  createdAt: any; // Firestore Timestamp
}

const createQuestion = (
  id: string,
  category: QuestionCategory,
  prompt: string,
  options: string[],
  correctAnswerIndex: number,
  difficulty: number = 3
): Question => ({
  id,
  locale: 'tr',
  category,
  difficulty,
  type: 'mcq',
  prompt,
  options,
  answer: options[correctAnswerIndex],
  approved: true,
  rand: Math.random()
});

