export const getRandomQuestion = (excludeIds: string[] = []): Question => {
  const availableQuestions = QUESTIONS.filter(q => !excludeIds.includes(q.id));
  const randomIndex = Math.floor(Math.random() * availableQuestions.length);
  return availableQuestions[randomIndex];
};

export const getQuestionsByCategory = (category: QuestionCategory): Question[] => {
  return QUESTIONS.filter(q => q.category === category);
};

// Firestore'daki tüm soruların used alanını sıfırla
export async function resetAllQuestionsUsage() {
  try {
    const { getFirebase } = await import('@/services/firebase');
    const { collection, getDocs, doc, updateDoc, writeBatch } = await import('firebase/firestore');
    
    const { db } = getFirebase();
    const batch = writeBatch(db);
    
    console.log('Tüm soruların used alanı sıfırlanıyor...');
    
    const questionsSnapshot = await getDocs(collection(db, 'questions'));
    
    questionsSnapshot.docs.forEach((docSnapshot) => {
      batch.update(doc(db, 'questions', docSnapshot.id), { used: false });
    });
    
    await batch.commit();
    
    console.log(`${questionsSnapshot.docs.length} sorunun used alanı sıfırlandı`);
    return { success: true, count: questionsSnapshot.docs.length };
  } catch (error) {
    console.error('Soruları sıfırlarken hata:', error);
    throw error;
  }
}

// Function to seed questions to Firebase
export async function seedQuestionsToFirebase() {
  try {
    const { getFirebase } = await import('@/services/firebase');
    const { collection, doc, setDoc, serverTimestamp } = await import('firebase/firestore');
    
    const { db } = getFirebase();
    
    console.log('Seeding questions to Firebase...');
    
    for (const question of QUESTIONS) {
      // Yeni yapıya uygun olarak kaydet
      await setDoc(doc(collection(db, 'questions'), question.id), {
        category: question.category,
        question: question.prompt,
        options: question.options,
        answer: question.answer, // Doğru cevabı string olarak sakla
        difficulty: question.difficulty,
        used: false, // Başlangıçta kullanılmamış
        createdAt: serverTimestamp()
      });
    }
    
    console.log(`Successfully seeded ${QUESTIONS.length} questions to Firebase`);
    return { success: true, count: QUESTIONS.length };
  } catch (error) {
    console.error('Error seeding questions:', error);
    throw error;
  }
}